}


.font-outfit {
  font-family: Outfit, sans-serif;
}

* {
  transition: transform .3s cubic-bezier(.4, 0, .2, 1);
}

::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-track {
  background: #1e293b;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(#8b5cf6, #06b6d4);
  border-radius: 3px;
}

::selection {
  color: #fff;
  background: #8b5cf64d;
}

@keyframes float {
  0%, 100% {
    transform: translateY(0)rotate(0);
  }

  50% {
    transform: translateY(-20px)rotate(180deg);
  }
}

@keyframes pulse-glow {
  0%, 100% {
    opacity: .4;
    transform: scale(1);
  }

  50% {
    opacity: .8;
    transform: scale(1.1);
  }
}

@keyframes marquee {
  0% {
    transform: translateX(0%);
  }

  100% {
    transform: translateX(-50%);
  }
}

@keyframes marquee-right-to-left {
  0% {
    transform: translateX(0%);
  }

  100% {
    transform: translateX(-100%);
  }
}

@keyframes marquee-left-to-right {
  0% {
    transform: translateX(-100%);
  }

  100% {
    transform: translateX(100%);
  }
}

.animate-marquee {
  animation: 30s linear infinite marquee;
}

.animate-marquee-rtl {
  animation: 40s linear infinite marquee-right-to-left;
}

.animate-marquee-ltr {
  animation: 50s linear infinite marquee-left-to-right;
}

.marquee-container:hover .animate-marquee-rtl, .marquee-container:hover .animate-marquee-ltr, .marquee-container:hover .animate-marquee {
  animation-play-state: paused;
}

.animate-marquee-rtl, .animate-marquee-ltr, .animate-marquee {
  transition: animation-play-state .3s;
}

@keyframes fadeUp {
  from {
    opacity: 0;
    transform: translateY(50px);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }
}

body.scroll-lock {
  height: 100vh;
  overflow: hidden;
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-100px);
  }

  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(100px);
  }

  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes drawLine {
  from {
    height: 0;
  }

  to {
    height: 100%;
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(.5);
  }

  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes countUp {
  from {
    opacity: 0;
    transform: translateY(20px);
