        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16 lg:mb-20"
        >
          <Badge className="bg-gradient-to-r from-purple-500 to-cyan-500 text-white border-0 mb-6">
