                  <p className="text-purple-400 font-medium mb-3">{leader.role}</p>

                  <div className="space-y-2 text-sm text-gray-300">
                    <p>{leader.experience}</p>
                    <p className="text-gray-400">{leader.education}</p>
                  </div>
                </ResponsiveCard>
              ))}
