                      </motion.div>
                    ))}
                  </div>
                </ResponsiveCard>
              ))}
            </ResponsiveCardGrid>
          </div>
        )}

        {/* Audit Documents */}
        {showDocuments && variant === 'full' && (
          <div className="mb-16 lg:mb-20">
            <motion.h3 
              className="text-2xl sm:text-3xl lg:text-4xl font-bold text-white text-center mb-8 lg:mb-12"
