                  className="absolute inset-0 bg-gradient-to-r from-transparent via-white/10 to-transparent -translate-x-full"
                  whileHover={{ 
                    translateX: "200%",
                    transition: { duration: 0.8, ease: "easeInOut" }
                  }}
                />

                <CardContent className="p-8 relative z-10">
                  {/* Icon & Metric */}
                  <div className="flex items-start justify-between mb-6">
                    <motion.div
                      className={`w-16 h-16 rounded-2xl bg-gradient-to-br ${service.gradient} p-3 relative overflow-hidden`}
                      whileHover={{ scale: 1.1, rotate: 5 }}
                      transition={{ type: "spring", stiffness: 300 }}
                    >
                      <service.icon className="w-full h-full text-white relative z-10" />
                      <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-700" />
                    </motion.div>

                    <div className="text-right">
                      <div className="text-2xl font-bold bg-gradient-to-r from-purple-400 to-cyan-400 bg-clip-text text-transparent">
                        {service.metrics.value}
                      </div>
                      <div className="text-xs font-medium text-gray-400 uppercase tracking-wide">
                        {service.metrics.label}
                      </div>
                    </div>
                  </div>

                  {/* Title & Description */}
                  <h3 className="font-outfit text-2xl font-bold text-white mb-4 group-hover:text-cyan-400 transition-colors duration-300">
                    {service.title}
                  </h3>

                  <p className="text-gray-300 mb-6 leading-relaxed">
                    {service.description}
                  </p>

                  {/* Features with staggered left-to-right animation */}
                  <div className="space-y-3 mb-6">
                    {service.features.map((feature, featureIndex) => (
                      <motion.div
                        key={feature}
                        className="flex items-center space-x-3 text-sm text-gray-400 group/feature"
                        initial={{ opacity: 0, x: -50 }}
                        animate={isInView ? { opacity: 1, x: 0 } : {}}
                        transition={{ 
                          duration: 0.8, 
                          delay: service.delay + 0.3 + (featureIndex * 0.15),
                          ease: [0.25, 0.25, 0, 1]
                        }}
                        whileHover={{ x: 5, transition: { duration: 0.2 } }}
                      >
                        <motion.div 
                          className={`w-2 h-2 rounded-full bg-gradient-to-r ${service.gradient}`}
                          whileHover={{ scale: 1.5 }}
                          transition={{ type: "spring", stiffness: 300 }}
                        />
                        <span className="group-hover/feature:text-gray-200 transition-colors duration-300">{feature}</span>
                      </motion.div>
                    ))}
                  </div>

                  {/* Hover CTA */}
                  <motion.div
                    className="opacity-0 group-hover:opacity-100 transition-all duration-300"
                    initial={{ y: 20 }}
                    whileHover={{ y: 0 }}
                  >
                    <button 
                      onClick={() => openPopup(service.popupType)}
                      className="flex items-center space-x-2 text-cyan-400 font-medium hover:text-white transition-colors duration-300"
                    >
                      <span>Explore Service</span>
                      <ArrowUpRight className="w-4 h-4 group-hover:translate-x-1 group-hover:-translate-y-1 transition-transform duration-300" />
                    </button>
                  </motion.div>
                </CardContent>

                {/* Magnetic Effect */}
                <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                  <div className={`absolute inset-0 bg-gradient-to-r ${service.gradient} opacity-5`} />
                </div>
              </Card>
            </motion.div>
          ))}
        </div>

        {/* Bottom Stats */}
        <motion.div
          className="glass rounded-3xl border border-white/10 p-12 relative overflow-hidden"
          initial={{ opacity: 0, y: 50 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 1, delay: 0.8 }}
        >
          {/* Background Effect */}
          <div className="absolute inset-0 bg-gradient-to-r from-purple-500/5 via-transparent to-cyan-500/5" />

          <div className="relative z-10 grid grid-cols-1 md:grid-cols-4 gap-8 text-center">
            {[
              { icon: '🚀', value: '24h', label: 'Response Time' },
              { icon: '📈', value: '300%', label: 'ROI Average' },
              { icon: '⭐', value: '500+', label: 'Happy Clients' },
              { icon: '🔒', value: '100%', label: 'Secure' }
            ].map((stat, index) => (
              <motion.div
                key={stat.label}
                className="group"
                initial={{ opacity: 0, y: 20 }}
                animate={isInView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.8, delay: 1.0 + (index * 0.1) }}
              >
                <div className="text-4xl mb-4">{stat.icon}</div>
                <div className="text-4xl font-bold bg-gradient-to-r from-purple-400 to-cyan-400 bg-clip-text text-transparent mb-2">
                  {stat.value}
                </div>
                <div className="text-gray-300 font-medium">{stat.label}</div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* CTA */}
        <motion.div
          className="text-center mt-16"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 1, delay: 1.2 }}
        >
          <p className="text-gray-300 mb-8 text-lg">
            Ready to transform your business with hypnotic digital experiences?
          </p>
          <motion.button
            className="relative px-12 py-4 gradient-electric hover:shadow-2xl hover:shadow-purple-500/30 text-white font-semibold rounded-2xl transition-all duration-300 group overflow-hidden"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => {
              const element = document.querySelector('#contact')
              if (element) {
                element.scrollIntoView({ behavior: 'smooth' })
              }
            }}
          >
            <span className="relative z-10 flex items-center space-x-2">
              <Sparkles className="w-5 h-5" />
              <span>Get Custom Quote</span>
            </span>
            <div className="absolute inset-0 bg-gradient-to-r from-cyan-500 to-purple-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
            <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-700" />
          </motion.button>
        </motion.div>
      </div>

      {/* Marketing Popups */}
      {popup.type && (
        <PopupModal
          isOpen={popup.isOpen}
          onClose={closePopup}
          title={getPopupTitle(popup.type)}
          maxWidth="max-w-6xl"
        >
          {(() => {
            const PopupComponent = getPopupComponent(popup.type)
            return PopupComponent ? <PopupComponent /> : null
          })()}
        </PopupModal>
      )}
    </section>
  )
}

client/src/components/TestimonialCard.ts.content:
'use client'

import { motion } from 'motion/react'
import { ResponsiveCard, ResponsiveCardHeader, ResponsiveCardContent, ResponsiveCardFooter } from './ui/responsive-card'
import { Avatar } from './ui/avatar'
import { Badge } from './ui/badge'
import { Star, Quote, ExternalLink, CheckCircle } from 'lucide-react'
import { ImageWithFallback } from './figma/ImageWithFallback'

interface TestimonialCardProps {
  testimonial: {
    id: string
    name: string
    role: string
    company: string
    avatar?: string
    content: string
    rating: number
    date: string
    verified?: boolean
    projectType?: string
    companyLogo?: string
    gradient?: string
  }
  index: number
  variant?: 'default' | 'featured' | 'compact'
}

export function TestimonialCard({ testimonial, index, variant = 'default' }: TestimonialCardProps) {
  const isCompact = variant === 'compact'
  const isFeatured = variant === 'featured'

  return (
    <ResponsiveCard
      variant={isFeatured ? 'gradient' : 'glass'}
      size={isCompact ? 'sm' : isFeatured ? 'xl' : 'md'}
      hover={true}
      animation={true}
      index={index}
      className={`group relative overflow-hidden ${isFeatured ? 'lg:col-span-2' : ''}`}
    >
      {/* Featured Badge */}
      {isFeatured && (
        <div className="absolute top-4 right-4 z-10">
          <Badge className="gradient-electric text-white border-0 shadow-lg">
            <CheckCircle className="w-3 h-3 mr-1" />
            Featured
          </Badge>
        </div>
      )}

      {/* Quote Icon */}
      <div className="absolute top-4 left-4 opacity-20 group-hover:opacity-30 transition-opacity duration-500">
        <Quote className={`${isCompact ? 'w-8 h-8' : 'w-12 h-12 sm:w-16 sm:h-16'} text-purple-400`} />
      </div>

      <ResponsiveCardHeader responsive={true}>
        <div className="flex flex-col sm:flex-row items-start space-y-3 sm:space-y-0 sm:space-x-4 w-full">
          {/* Avatar */}
          <div className="flex-shrink-0">
            <div className={`${isCompact ? 'w-10 h-10' : 'w-12 h-12 sm:w-16 sm:h-16'} rounded-full overflow-hidden ring-2 ring-white/20 group-hover:ring-purple-400/50 transition-all duration-500`}>
              {testimonial.avatar ? (
                <ImageWithFallback
                  src={testimonial.avatar}
                  alt={testimonial.name}
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full bg-gradient-to-br from-purple-500 to-cyan-500 flex items-center justify-center text-white font-bold">
                  {testimonial.name.split(' ').map(n => n[0]).join('')}
                </div>
              )}
            </div>
          </div>

          {/* User Info */}
          <div className="flex-1 min-w-0">
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between w-full">
              <div className="min-w-0">
                <h3 className={`${isCompact ? 'text-sm' : 'text-base sm:text-lg'} font-semibold text-white group-hover:text-purple-300 transition-colors duration-300 truncate`}>
                  {testimonial.name}
                </h3>
                <p className={`${isCompact ? 'text-xs' : 'text-sm'} text-gray-400 truncate`}>
                  {testimonial.role}
                </p>
                <div className="flex items-center space-x-2 mt-1">
                  <p className={`${isCompact ? 'text-xs' : 'text-sm'} font-medium text-purple-400 truncate`}>
                    {testimonial.company}
                  </p>
                  {testimonial.verified && (
                    <CheckCircle className="w-3 h-3 text-green-400 flex-shrink-0" />
                  )}
                </div>
              </div>

              {/* Company Logo */}
              {testimonial.companyLogo && !isCompact && (
                <div className="mt-2 sm:mt-0 flex-shrink-0">
                  <div className="w-8 h-8 sm:w-10 sm:h-10 rounded-lg overflow-hidden bg-white/10 p-1">
                    <ImageWithFallback
                      src={testimonial.companyLogo}
                      alt={`${testimonial.company} logo`}
                      className="w-full h-full object-contain"
                    />
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Rating & Meta */}
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between w-full mt-3 space-y-2 sm:space-y-0">
          {/* Rating */}
          <div className="flex items-center space-x-2">
            <div className="flex items-center space-x-0.5">
              {[...Array(5)].map((_, i) => (
                <motion.div
                  key={i}
                  initial={{ opacity: 0, scale: 0 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.3, delay: 0.5 + i * 0.1 }}
                >
                  <Star 
                    className={`${isCompact ? 'w-3 h-3' : 'w-4 h-4'} ${i < testimonial.rating ? 'text-yellow-400 fill-current' : 'text-gray-600'}`} 
                  />
                </motion.div>
              ))}
            </div>
            <span className={`${isCompact ? 'text-xs' : 'text-sm'} text-gray-400`}>
              ({testimonial.rating}/5)
            </span>
          </div>

          {/* Project Type & Date */}
          <div className="flex items-center space-x-2 sm:space-x-3">
            {testimonial.projectType && (
              <Badge variant="outline" className="border-white/20 text-gray-300 text-xs">
                {testimonial.projectType}
              </Badge>
            )}
            <span className={`${isCompact ? 'text-xs' : 'text-sm'} text-gray-500`}>
              {new Date(testimonial.date).toLocaleDateString('en-US', {
                month: 'short',
                year: 'numeric'
              })}
            </span>
          </div>
        </div>
      </ResponsiveCardHeader>

      <ResponsiveCardContent spacing={isCompact ? 'sm' : 'md'}>
        {/* Testimonial Content */}
        <div className="relative">
          <blockquote className={`${isCompact ? 'text-sm' : 'text-base lg:text-lg'} text-gray-300 leading-relaxed italic group-hover:text-gray-200 transition-colors duration-300`}>
            "{testimonial.content}"
          </blockquote>
        </div>
      </ResponsiveCardContent>

      {!isCompact && (
        <ResponsiveCardFooter direction="responsive">
          <div className="flex items-center justify-between w-full">
            <div className="text-xs text-gray-500">
              Verified Review
            </div>

            <button className="flex items-center space-x-1 text-xs text-purple-400 hover:text-purple-300 transition-colors duration-300 group/link">
              <span>View Project</span>
              <ExternalLink className="w-3 h-3 group-hover/link:translate-x-0.5 group-hover/link:-translate-y-0.5 transition-transform duration-300" />
            </button>
          </div>
        </ResponsiveCardFooter>
      )}

      {/* Background Pattern */}
      {testimonial.gradient && (
        <div className={`absolute inset-0 bg-gradient-to-br ${testimonial.gradient} opacity-5 group-hover:opacity-10 transition-opacity duration-500`} />
      )}

      {/* Hover Effect */}
      <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/5 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-1000 pointer-events-none" />
    </ResponsiveCard>
  )
}

// Testimonials Grid Component
interface TestimonialsGridProps {
  testimonials: TestimonialCardProps['testimonial'][]
  variant?: 'default' | 'masonry' | 'featured'
  columns?: 1 | 2 | 3 | 4
}

export function TestimonialsGrid({ testimonials, variant = 'default', columns = 3 }: TestimonialsGridProps) {
  if (variant === 'masonry') {
    return (
      <div className={`columns-1 md:columns-2 ${columns >= 3 ? 'lg:columns-3' : ''} ${columns >= 4 ? 'xl:columns-4' : ''} gap-4 sm:gap-6 lg:gap-8 space-y-4 sm:space-y-6 lg:space-y-8`}>
        {testimonials.map((testimonial, index) => (
          <div key={testimonial.id} className="break-inside-avoid">
            <TestimonialCard
              testimonial={testimonial}
              index={index}
              variant={index % 5 === 0 ? 'featured' : 'default'}
            />
          </div>
        ))}
      </div>
    )
  }

  return (
    <div className={`grid grid-cols-1 md:grid-cols-2 ${columns >= 3 ? 'lg:grid-cols-3' : ''} ${columns >= 4 ? 'xl:grid-cols-4' : ''} gap-4 sm:gap-6 lg:gap-8`}>
      {testimonials.map((testimonial, index) => (
        <TestimonialCard
          key={testimonial.id}
          testimonial={testimonial}
          index={index}
          variant={variant === 'featured' && index === 0 ? 'featured' : 'default'}
        />
      ))}
    </div>
  )
}

client/src/components/TestimonialMarquee.content:
"use client";

import React, { useRef } from "react";
import { motion, useAnimation, useInView } from "motion/react";
import { Users, Star } from "lucide-react";
import { testimonials } from "../data/testimonials";

export function TestimonialMarquee({
  featuredTestimonials = testimonials.slice(0, 6),
}) {
  const testimonialsRef = useRef(null);
  const controls = useAnimation();
  const isInView = useInView(testimonialsRef, {
    once: true,
    amount: 0.2,
  });

  return (
    <section
      ref={testimonialsRef}
      className="py-12 relative overflow-hidden"
    >
      <div className="max-w-7xl mx-auto px-6 lg:px-8 relative z-10">
        {/* Section Header */}
        <motion.div
          className="text-center mb-20"
          initial={{ opacity: 0, y: 50 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 1, ease: [0.25, 0.25, 0, 1] }}
        >
          <motion.div
            className="inline-flex items-center space-x-3 glass rounded-full px-6 py-3 mb-8 border border-white/20"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={isInView ? { opacity: 1, scale: 1 } : {}}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <Users className="w-5 h-5 text-purple-400" />
            <span className="font-medium text-gray-200">
              Client Love
            </span>
          </motion.div>
          <h2 className="font-outfit text-4xl lg:text-5xl font-bold mb-8 leading-tight">
            <span className="bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
              What Our Clients
            </span>
            <br />
            <span className="bg-gradient-to-r from-purple-400 via-pink-400 to-cyan-400 bg-clip-text text-transparent">
              Say About Us
            </span>
          </h2>
        </motion.div>

        {/* Enhanced Right-to-Left Marquee with Professional Hover Pause */}
        <motion.div
          className="marquee-container relative overflow-hidden w-full h-[400px]"
          initial={{ opacity: 0 }}
          animate={isInView ? { opacity: 1 } : {}}
          transition={{ duration: 1, delay: 0.5 }}
        >
          <div
            className="animate-marquee-rtl flex gap-6"
            style={{ animationDuration: "60s" }}
          >
            {[
              ...featuredTestimonials,
              ...featuredTestimonials,
              ...featuredTestimonials,
            ].map((testimonial, index) => (
              <div
                key={`${testimonial.id}-${index}`}
                className="w-[90vw] sm:w-[300px] lg:w-[400px] max-w-full perspective flex-shrink-0"
              >
                <motion.div
                  className="relative w-full h-full transition-transform duration-700 card-flip-inner"
                  style={{ transformStyle: "preserve-3d" }}
                  whileHover={{
                    rotateY: 180,
                    scale: 1.03,
                    y: -10,
                  }}
                  transition={{
                    type: "spring",
                    stiffness: 200,
                    damping: 20,
                  }}
                >
                  {/* Front Side */}
                  <div
                    className="break-words absolute inset-0 glass border border-white/10 rounded-xl p-6 shadow-lg hover:shadow-purple-500/20 glow-on-hover card-front"
                    style={{
                      backfaceVisibility: "hidden",
                      WebkitBackfaceVisibility: "hidden",
                    }}
                  >
                    <div className="text-6xl text-purple-400/30 mb-4">
                      "
                    </div>
                    <blockquote className="break-words text-gray-300 text-base md:text-lg italic leading-relaxed border-l-4 border-purple-500 pl-4 mb-6">
                      {testimonial.content}
                    </blockquote>

                    <div className="flex items-center space-x-1 mb-6">
                      {[...Array(5)].map((_, i) => (
                        <Star
                          key={i}
                          className="w-5 h-5 text-yellow-400 fill-current"
                        />
                      ))}
                    </div>
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 gradient-electric rounded-full flex items-center justify-center">
                        <span className="text-white font-bold text-lg">
                          {testimonial.name?.charAt(0) || "A"}
                        </span>
                      </div>
                      <div>
                        <div className="font-semibold text-white">
                          {testimonial.name}
                        </div>
                        <div className="text-gray-400 text-sm">
                          {testimonial.role}
                        </div>
                        <div className="text-purple-400 text-sm">
                          {testimonial.company}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Back Side */}
                  <div
                    className="absolute inset-0 glass border border-white/10 rounded-xl p-6 bg-gradient-to-br from-purple-900 via-pink-900 to-cyan-900 text-white shadow-lg card-back"
                    style={{
                      transform: "rotateY(180deg)",
                      backfaceVisibility: "hidden",
                      WebkitBackfaceVisibility: "hidden",
                    }}
                  >
                    <h4 className="text-xl font-semibold text-cyan-300 mb-6 flex items-center gap-2">
                      Project Impact Summary
                      <span className="h-[1px] flex-1 bg-white/10"></span>
                    </h4>

