client/src/pages/AuthPage.tsx.content:
'use client'

import { useState, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Eye, EyeOff, Mail, Lock, User, ArrowRight, Sparkles, Github, Chrome, Shield, Zap } from 'lucide-react'
import { Button } from '../components/ui/button'
import { Input } from '../components/ui/input'
import { Label } from '../components/ui/label'
import { Card } from '../components/ui/card'
import { Separator } from '../components/ui/separator'

export function AuthPage() {
  const [isLogin, setIsLogin] = useState(true)
  const [showPassword, setShowPassword] = useState(false)
  const [isLoading, setIsLoading] = useState(false)
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: ''
  })

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setIsLoading(true)

    // Simulate authentication
    await new Promise(resolve => setTimeout(resolve, 2000))

    setIsLoading(false)
    // Handle success/error states here
  }

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }))
  }

  const toggleMode = () => {
    setIsLogin(!isLogin)
    setFormData({
      email: '',
      password: '',
      confirmPassword: '',
      firstName: '',
      lastName: ''
    })
  }

  return (
    <div className="min-h-screen bg-slate-950 relative overflow-hidden flex items-center justify-center px-4 py-20">
      {/* Animated Background */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute inset-0 bg-gradient-to-br from-slate-900 via-purple-900/20 to-slate-900"></div>

        {/* Floating orbs */}
        <div className="absolute top-1/4 left-1/4 w-72 h-72 bg-purple-500/10 rounded-full blur-3xl animate-float"></div>
        <div 
          className="absolute top-3/4 right-1/4 w-96 h-96 bg-cyan-500/10 rounded-full blur-3xl animate-float" 
          style={{ animationDelay: '2s' }}
        ></div>
        <div 
          className="absolute top-1/2 left-1/2 w-64 h-64 bg-pink-500/10 rounded-full blur-3xl animate-float" 
          style={{ animationDelay: '4s' }}
        ></div>

        {/* Grid pattern */}
        <div className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.02)_1px,transparent_1px)] bg-[size:50px_50px]"></div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 w-full max-w-6xl mx-auto">
        <div className="grid lg:grid-cols-2 gap-12 items-center">

          {/* Left Side - Marketing Content */}
          <motion.div 
            className="hidden lg:block space-y-8"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <div className="space-y-6">
              <motion.div
                className="flex items-center space-x-3"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.2 }}
              >
                <div className="w-12 h-12 gradient-electric rounded-xl flex items-center justify-center relative overflow-hidden">
                  <span className="font-outfit text-white font-bold text-xl relative z-10">C</span>
                  <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -skew-x-12 animate-pulse-glow"></div>
                </div>
                <span className="font-outfit text-3xl font-bold bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
                  CodeFlow
                </span>
              </motion.div>

              <motion.h1 
                className="text-5xl font-outfit font-bold text-white leading-tight"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
              >
                Welcome to the
                <span className="block bg-gradient-to-r from-purple-400 via-pink-400 to-cyan-400 bg-clip-text text-transparent">
                  Future of Web
                </span>
              </motion.h1>

              <motion.p 
                className="text-xl text-gray-300 leading-relaxed"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.4 }}
              >
                Join thousands of developers and businesses who trust CodeFlow for cutting-edge 
                web solutions that drive innovation and growth.
              </motion.p>
            </div>

            {/* Feature highlights */}
            <motion.div 
              className="space-y-4"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5 }}
            >
              {[
                { icon: Shield, text: "Enterprise-grade security" },
                { icon: Zap, text: "Lightning-fast performance" },
                { icon: Sparkles, text: "AI-powered development" }
              ].map((feature, index) => (
                <motion.div 
                  key={index}
                  className="flex items-center space-x-3 text-gray-300"
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.6 + index * 0.1 }}
                >
                  <div className="w-8 h-8 glass rounded-lg flex items-center justify-center">
                    <feature.icon className="w-4 h-4 text-purple-400" />
                  </div>
                  <span>{feature.text}</span>
                </motion.div>
              ))}
            </motion.div>

            {/* Stats */}
            <motion.div 
              className="grid grid-cols-3 gap-6 pt-8"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.8 }}
            >
              {[
                { value: "10K+", label: "Active Users" },
                { value: "99.9%", label: "Uptime" },
                { value: "50+", label: "Countries" }
              ].map((stat, index) => (
                <div key={index} className="text-center">
                  <div className="text-2xl font-bold text-white">{stat.value}</div>
                  <div className="text-sm text-gray-400">{stat.label}</div>
                </div>
              ))}
            </motion.div>
          </motion.div>

          {/* Right Side - Auth Form */}
          <motion.div
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            className="w-full max-w-md mx-auto"
          >
            <Card className="glass border-white/10 p-8 relative overflow-hidden">
              {/* Card glow effect */}
              <div className="absolute inset-0 bg-gradient-to-r from-purple-500/5 via-transparent to-cyan-500/5 pointer-events-none"></div>

              <div className="relative z-10">
                {/* Header */}
                <div className="text-center mb-8">
                  <motion.h2 
                    className="text-3xl font-outfit font-bold text-white mb-2"
                    key={isLogin ? 'login' : 'signup'}
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3 }}
                  >
                    {isLogin ? 'Welcome Back' : 'Create Account'}
                  </motion.h2>
                  <motion.p 
                    className="text-gray-400"
                    key={isLogin ? 'login-sub' : 'signup-sub'}
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3, delay: 0.1 }}
                  >
                    {isLogin ? 'Sign in to your account' : 'Join the CodeFlow community'}
                  </motion.p>
                </div>

                {/* Social Login */}
                <div className="space-y-3 mb-6">
                  <Button 
                    variant="outline" 
                    className="w-full glass border-white/20 hover:border-white/30 text-white hover:bg-white/5 transition-all duration-300"
                  >
                    <Github className="w-4 h-4 mr-2" />
                    Continue with GitHub
                  </Button>
                  <Button 
                    variant="outline" 
                    className="w-full glass border-white/20 hover:border-white/30 text-white hover:bg-white/5 transition-all duration-300"
                  >
                    <Chrome className="w-4 h-4 mr-2" />
                    Continue with Google
                  </Button>
                </div>

                <div className="relative mb-6">
                  <Separator className="bg-white/10" />
                  <div className="absolute inset-0 flex items-center justify-center">
                    <span className="bg-slate-950 px-4 text-sm text-gray-400">or</span>
                  </div>
                </div>

                {/* Form */}
                <form onSubmit={handleSubmit} className="space-y-6">
                  <AnimatePresence mode="wait">
                    {!isLogin && (
                      <motion.div
                        initial={{ opacity: 0, height: 0 }}
                        animate={{ opacity: 1, height: 'auto' }}
                        exit={{ opacity: 0, height: 0 }}
                        transition={{ duration: 0.3 }}
                        className="grid grid-cols-2 gap-4"
                      >
                        <div className="space-y-2">
                          <Label htmlFor="firstName" className="text-gray-300">First Name</Label>
                          <div className="relative">
                            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                            <Input
                              id="firstName"
                              type="text"
                              placeholder="John"
                              value={formData.firstName}
                              onChange={(e) => handleInputChange('firstName', e.target.value)}
                              className="pl-10 glass border-white/20 bg-white/5 text-white placeholder:text-gray-500 focus:border-purple-400 focus:ring-purple-400/20"
                            />
                          </div>
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor="lastName" className="text-gray-300">Last Name</Label>
                          <Input
                            id="lastName"
                            type="text"
                            placeholder="Doe"
                            value={formData.lastName}
                            onChange={(e) => handleInputChange('lastName', e.target.value)}
                            className="glass border-white/20 bg-white/5 text-white placeholder:text-gray-500 focus:border-purple-400 focus:ring-purple-400/20"
                          />
                        </div>
                      </motion.div>
                    )}
                  </AnimatePresence>

                  <div className="space-y-2">
                    <Label htmlFor="email" className="text-gray-300">Email</Label>
                    <div className="relative">
                      <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                      <Input
                        id="email"
                        type="email"
                        placeholder="john@example.com"
                        value={formData.email}
                        onChange={(e) => handleInputChange('email', e.target.value)}
                        className="pl-10 glass border-white/20 bg-white/5 text-white placeholder:text-gray-500 focus:border-purple-400 focus:ring-purple-400/20"
                        required
                      />
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="password" className="text-gray-300">Password</Label>
                    <div className="relative">
                      <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                      <Input
                        id="password"
                        type={showPassword ? 'text' : 'password'}
                        placeholder="Enter your password"
                        value={formData.password}
                        onChange={(e) => handleInputChange('password', e.target.value)}
                        className="pl-10 pr-10 glass border-white/20 bg-white/5 text-white placeholder:text-gray-500 focus:border-purple-400 focus:ring-purple-400/20"
                        required
                      />
                      <button
                        type="button"
                        onClick={() => setShowPassword(!showPassword)}
                        className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white transition-colors"
                      >
                        {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                      </button>
                    </div>
                  </div>

                  <AnimatePresence mode="wait">
                    {!isLogin && (
                      <motion.div
                        initial={{ opacity: 0, height: 0 }}
                        animate={{ opacity: 1, height: 'auto' }}
                        exit={{ opacity: 0, height: 0 }}
                        transition={{ duration: 0.3 }}
                        className="space-y-2"
                      >
                        <Label htmlFor="confirmPassword" className="text-gray-300">Confirm Password</Label>
                        <div className="relative">
                          <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                          <Input
                            id="confirmPassword"
                            type="password"
                            placeholder="Confirm your password"
                            value={formData.confirmPassword}
                            onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
                            className="pl-10 glass border-white/20 bg-white/5 text-white placeholder:text-gray-500 focus:border-purple-400 focus:ring-purple-400/20"
                          />
                        </div>
                      </motion.div>
                    )}
                  </AnimatePresence>

                  {isLogin && (
                    <div className="flex items-center justify-between text-sm">
                      <label className="flex items-center text-gray-300">
                        <input type="checkbox" className="mr-2 rounded border-gray-600 bg-white/5" />
                        Remember me
                      </label>
                      <button type="button" className="text-purple-400 hover:text-purple-300 transition-colors">
                        Forgot password?
                      </button>
                    </div>
                  )}

                  <Button
                    type="submit"
                    disabled={isLoading}
                    className="w-full gradient-electric hover:shadow-lg hover:shadow-purple-500/25 text-white font-semibold py-3 transition-all duration-300 magnetic group"
                  >
                    {isLoading ? (
                      <div className="flex items-center space-x-2">
                        <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                        <span>Processing...</span>
                      </div>
                    ) : (
                      <div className="flex items-center space-x-2">
                        <span>{isLogin ? 'Sign In' : 'Create Account'}</span>
                        <ArrowRight className="w-4 h-4 transition-transform group-hover:translate-x-1" />
                      </div>
                    )}
                  </Button>
                </form>

                {/* Toggle */}
                <div className="mt-8 text-center">
                  <p className="text-gray-400">
                    {isLogin ? "Don't have an account?" : "Already have an account?"}
                    {' '}
                    <button
                      type="button"
                      onClick={toggleMode}
                      className="text-purple-400 hover:text-purple-300 font-medium transition-colors"
                    >
                      {isLogin ? 'Sign up' : 'Sign in'}
                    </button>
                  </p>
                </div>

                {/* Terms */}
                {!isLogin && (
                  <motion.p 
                    className="mt-6 text-xs text-gray-500 text-center"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.5 }}
                  >
                    By creating an account, you agree to our{' '}
                    <button className="text-purple-400 hover:text-purple-300">Terms of Service</button>
                    {' '}and{' '}
                    <button className="text-purple-400 hover:text-purple-300">Privacy Policy</button>
                  </motion.p>
                )}
              </div>
            </Card>
          </motion.div>
        </div>
      </div>
    </div>
  )
}

client/src/pages/BlogPage.tsx.content:
'use client'

import { useState, useEffect, useRef } from 'react'
import { motion } from 'motion/react'
import { blogPosts, featuredPosts, blogCategories, BlogPost, BlogCategory, getPostBySlug } from '../data/blog'
import { Search, Calendar, Clock, ArrowRight, Filter, X } from 'lucide-react'
import { ImageWithFallback } from '../components/figma/ImageWithFallback'
import { BlogArticle } from '../components/BlogArticle'

export function BlogPage() {
  const [selectedCategory, setSelectedCategory] = useState<string>('all')
  const [searchQuery, setSearchQuery] = useState('')
  const [filteredPosts, setFilteredPosts] = useState<BlogPost[]>(blogPosts)
  const [isSearchOpen, setIsSearchOpen] = useState(false)
  const [visiblePosts, setVisiblePosts] = useState<Set<string>>(new Set())
  const [selectedPost, setSelectedPost] = useState<BlogPost | null>(null)
  const observerRef = useRef<IntersectionObserver | null>(null)

  // Filter posts based on category and search
  useEffect(() => {
    let filtered = blogPosts

    if (selectedCategory !== 'all') {
      filtered = filtered.filter(post => post.category === selectedCategory)
    }

    if (searchQuery) {
      filtered = filtered.filter(post => 
        post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        post.excerpt.toLowerCase().includes(searchQuery.toLowerCase()) ||
        post.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()))
      )
    }

    setFilteredPosts(filtered)
  }, [selectedCategory, searchQuery])

  // Intersection observer for scroll animations
  useEffect(() => {
    observerRef.current = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setVisiblePosts(prev => new Set([...prev, entry.target.id]))
          }
        })
      },
      { threshold: 0.1 }
    )

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect()
      }
    }
  }, [])

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    })
  }

  const handlePostClick = (post: BlogPost) => {
    setSelectedPost(post)
  }

  const handleBackToList = () => {
    setSelectedPost(null)
  }

  // If a post is selected, show the article view
  if (selectedPost) {
    return <BlogArticle post={selectedPost} onBack={handleBackToList} />
  }

  return (
    <div className="min-h-screen bg-slate-950 text-white pt-20">
      {/* Hero Section with Featured Posts */}
      <section className="relative py-20 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-purple-900/20 via-slate-900 to-cyan-900/20"></div>
        <div className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.02)_1px,transparent_1px)] bg-[size:50px_50px]"></div>

        <div className="relative z-10 max-w-7xl mx-auto px-6 lg:px-8">
          {/* Hero Header */}
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center mb-20"
          >
            <h1 className="font-outfit text-6xl lg:text-7xl font-bold mb-8">
              <span className="bg-gradient-to-r from-white via-purple-200 to-cyan-200 bg-clip-text text-transparent">
                Insights & 
              </span>
              <br />
              <span className="bg-gradient-to-r from-purple-400 to-cyan-400 bg-clip-text text-transparent animate-pulse-glow">
                Innovation
              </span>
            </h1>
            <p className="text-xl text-gray-300 max-w-3xl mx-auto leading-relaxed">
              Explore the latest trends, insights, and innovations in technology, design, and development. 
              Stay ahead of the curve with our expert perspectives.
            </p>
          </motion.div>

          {/* Featured Posts Grid */}
          <div className="grid lg:grid-cols-2 gap-8 mb-20">
            {featuredPosts.map((post, index) => (
              <motion.article
                key={post.id}
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: index * 0.2 }}
                onClick={() => handlePostClick(post)}
                className="group relative glass rounded-2xl overflow-hidden hover-glow cursor-pointer"
                style={{
                  background: 'rgba(255, 255, 255, 0.05)',
                  backdropFilter: 'blur(20px)',
                }}
              >
                <div className="aspect-video relative overflow-hidden">
                  <ImageWithFallback
                    src={post.image}
                    alt={post.title}
                    className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent"></div>
                  <div className="absolute top-4 left-4">
                    <span className="px-3 py-1 bg-purple-500/80 backdrop-blur-sm rounded-full text-sm font-medium">
                      Featured
                    </span>
                  </div>
                </div>

                <div className="p-8">
                  <div className="flex items-center space-x-4 mb-4 text-sm text-gray-400">
                    <div className="flex items-center space-x-2">
                      <Calendar className="w-4 h-4" />
                      <span>{formatDate(post.publishedAt)}</span>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Clock className="w-4 h-4" />
                      <span>{post.readTime} min read</span>
                    </div>
                  </div>

                  <h2 className="font-outfit text-2xl font-bold mb-4 group-hover:text-purple-300 transition-colors duration-300">
                    {post.title}
                  </h2>

                  <p className="text-gray-300 mb-6 leading-relaxed">
                    {post.excerpt}
                  </p>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 rounded-full overflow-hidden">
                        <ImageWithFallback
                          src={post.author.avatar}
                          alt={post.author.name}
                          className="w-full h-full object-cover"
                        />
                      </div>
                      <div>
                        <p className="font-medium">{post.author.name}</p>
                        <p className="text-sm text-gray-400">{post.author.role}</p>
                      </div>
                    </div>

                    <div className="flex items-center space-x-2 text-purple-400 hover:text-white transition-colors duration-300 group">
                      <span>Read More</span>
                      <ArrowRight className="w-4 h-4 group-hover:translate-x-1 transition-transform duration-300" />
                    </div>
                  </div>
                </div>
              </motion.article>
            ))}
          </div>
        </div>
      </section>

      {/* Filter and Search Section */}
      <section className="relative py-12 border-t border-white/10">
        <div className="max-w-7xl mx-auto px-6 lg:px-8">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between space-y-6 lg:space-y-0">
            {/* Category Filters */}
            <div className="flex flex-wrap gap-3">
              <button
                onClick={() => setSelectedCategory('all')}
                className={`px-6 py-3 rounded-xl font-medium transition-all duration-300 ${
                  selectedCategory === 'all'
                    ? 'gradient-electric text-white'
                    : 'glass text-gray-300 hover:text-white'
                }`}
              >
                All Articles
              </button>
              {blogCategories.map((category) => (
                <button
                  key={category.id}
                  onClick={() => setSelectedCategory(category.id)}
                  className={`px-6 py-3 rounded-xl font-medium transition-all duration-300 ${
                    selectedCategory === category.id
                      ? 'gradient-electric text-white'
                      : 'glass text-gray-300 hover:text-white'
                  }`}
                >
                  {category.name}
                </button>
              ))}
            </div>

            {/* Search */}
            <div className="relative">
              <button
                onClick={() => setIsSearchOpen(!isSearchOpen)}
                className="flex items-center space-x-2 glass px-6 py-3 rounded-xl text-gray-300 hover:text-white transition-all duration-300"
              >
                <Search className="w-5 h-5" />
                <span>Search Articles</span>
              </button>

              {isSearchOpen && (
                <motion.div
                  initial={{ opacity: 0, scale: 0.95 }}
                  animate={{ opacity: 1, scale: 1 }}
                  className="absolute top-full right-0 mt-2 w-80 glass rounded-xl p-4 z-20"
                >
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                    <input
                      type="text"
                      placeholder="Search articles, tags..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      className="w-full pl-10 pr-10 py-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:border-purple-400 transition-colors duration-300"
                      autoFocus
                    />
                    <button
                      onClick={() => {
                        setSearchQuery('')
                        setIsSearchOpen(false)
                      }}
                      className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white transition-colors duration-300"
                    >
                      <X className="w-5 h-5" />
                    </button>
                  </div>
                </motion.div>
              )}
            </div>
          </div>
        </div>
      </section>

      {/* Articles Grid */}
      <section className="relative py-20">
        <div className="max-w-7xl mx-auto px-6 lg:px-8">
          {filteredPosts.length === 0 ? (
            <motion.div
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              className="text-center py-20"
            >
              <div className="w-20 h-20 mx-auto mb-6 glass rounded-2xl flex items-center justify-center">
                <Search className="w-8 h-8 text-gray-400" />
              </div>
              <h3 className="font-outfit text-2xl font-bold mb-4">No articles found</h3>
              <p className="text-gray-400 mb-8">Try adjusting your search or filter criteria.</p>
              <button
                onClick={() => {
                  setSearchQuery('')
                  setSelectedCategory('all')
                }}
                className="px-6 py-3 gradient-electric rounded-xl font-medium hover:scale-105 transition-transform duration-300"
              >
                Reset Filters
              </button>
            </motion.div>
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredPosts.map((post, index) => (
                <motion.article
                  key={post.id}
                  id={`post-${post.id}`}
                  ref={(el) => {
                    if (el && observerRef.current) {
                      observerRef.current.observe(el)
                    }
                  }}
                  initial={{ opacity: 0, y: 50 }}
                  animate={{ 
                    opacity: visiblePosts.has(`post-${post.id}`) ? 1 : 0,
                    y: visiblePosts.has(`post-${post.id}`) ? 0 : 50
                  }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  className="group glass rounded-2xl overflow-hidden hover:scale-105 hover-glow transition-all duration-500 cursor-pointer"
                  onClick={() => handlePostClick(post)}
                >
                  <div className="aspect-video relative overflow-hidden">
                    <ImageWithFallback
                      src={post.image}
                      alt={post.title}
                      className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent"></div>
                    <div className="absolute top-4 left-4">
                      <span className={`px-3 py-1 bg-gradient-to-r ${
                        blogCategories.find(cat => cat.id === post.category)?.color || 'from-purple-500 to-pink-500'
                      } rounded-full text-xs font-medium`}>
                        {blogCategories.find(cat => cat.id === post.category)?.name}
                      </span>
                    </div>
                  </div>

                  <div className="p-6">
                    <div className="flex items-center space-x-4 mb-3 text-sm text-gray-400">
                      <div className="flex items-center space-x-1">
                        <Calendar className="w-4 h-4" />
                        <span>{formatDate(post.publishedAt)}</span>
                      </div>
                      <div className="flex items-center space-x-1">
                        <Clock className="w-4 h-4" />
                        <span>{post.readTime}m</span>
                      </div>
                    </div>

                    <h3 className="font-outfit text-xl font-bold mb-3 group-hover:text-purple-300 transition-colors duration-300 line-clamp-2">
                      {post.title}
                    </h3>

                    <p className="text-gray-300 mb-4 line-clamp-3 leading-relaxed">
                      {post.excerpt}
                    </p>

                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <div className="w-8 h-8 rounded-full overflow-hidden">
                          <ImageWithFallback
                            src={post.author.avatar}
                            alt={post.author.name}
                            className="w-full h-full object-cover"
                          />
                        </div>
                        <span className="text-sm text-gray-400">{post.author.name}</span>
                      </div>

                      <ArrowRight className="w-5 h-5 text-purple-400 group-hover:translate-x-1 transition-transform duration-300" />
                    </div>
                  </div>
                </motion.article>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Newsletter Subscription */}
      <section className="relative py-20 border-t border-white/10">
        <div className="absolute inset-0 bg-gradient-to-br from-purple-900/10 to-cyan-900/10"></div>
        <div className="relative z-10 max-w-4xl mx-auto px-6 lg:px-8 text-center">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <h2 className="font-outfit text-4xl lg:text-5xl font-bold mb-6">
              <span className="bg-gradient-to-r from-purple-400 to-cyan-400 bg-clip-text text-transparent">
                Stay in the Loop
              </span>
            </h2>
            <p className="text-xl text-gray-300 mb-10 leading-relaxed">
              Get the latest insights, trends, and best practices delivered directly to your inbox. 
              Join thousands of developers and designers who trust our content.
            </p>

            <div className="flex flex-col sm:flex-row gap-4 max-w-md mx-auto">
              <input
                type="email"
                placeholder="Enter your email"
                className="flex-1 px-6 py-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:border-purple-400 transition-colors duration-300"
              />
              <button className="px-8 py-4 gradient-electric rounded-xl font-medium hover:scale-105 transition-transform duration-300 whitespace-nowrap">
                Subscribe
              </button>
            </div>

            <p className="text-sm text-gray-400 mt-4">
              No spam, unsubscribe at any time. We respect your privacy.
            </p>
          </motion.div>
        </div>
      </section>
    </div>
  )
}

client/src/pages/CareersPage.tsx.content:
"use client";

import { useState, useRef } from "react";
import {
  motion,
  useInView,
  AnimatePresence,
} from "motion/react";
import {
  ChevronDown,
  MapPin,
  Clock,
  Users,
  Star,
  ArrowRight,
  Briefcase,
  Heart,
  Zap,
  Globe,
  Award,
  TrendingUp,
  Coffee,
  Calendar,
} from "lucide-react";
import { Card, CardContent } from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { Button } from "../components/ui/button";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../components/ui/accordion";
import {
  jobPositions,
  companyPerks,
  cultureImages,
  careerStats,
  missionStatement,
} from "../data/careers";
import { ImageWithFallback } from "../components/figma/ImageWithFallback";

export function CareersPage() {
  const [selectedDepartment, setSelectedDepartment] =
    useState<string>("all");
  const [expandedJob, setExpandedJob] = useState<string | null>(
    null,
  );

  const heroRef = useRef(null);
  const jobsRef = useRef(null);
  const cultureRef = useRef(null);
  const perksRef = useRef(null);

  const isHeroInView = useInView(heroRef, {
    once: true,
    amount: 0.2,
  });
  const isJobsInView = useInView(jobsRef, {
    once: true,
    amount: 0.1,
  });
  const isCultureInView = useInView(cultureRef, {
    once: true,
    amount: 0.1,
  });
  const isPerksInView = useInView(perksRef, {
    once: true,
    amount: 0.1,
  });

  const departments = [
    "all",
    ...Array.from(
      new Set(jobPositions.map((job) => job.department)),
    ),
  ];

  const filteredJobs =
    selectedDepartment === "all"
      ? jobPositions
      : jobPositions.filter(
          (job) => job.department === selectedDepartment,
        );

  const featuredJobs = jobPositions.filter(
    (job) => job.featured,
  );

  const formatSalary = (
    min: number,
    max: number,
    currency: string,
  ) => {
    if (currency === "USD" && min >= 1000) {
      return `$${min.toLocaleString()} - $${max.toLocaleString()}`;
    }
    return `$${min}/hr - $${max}/hr`;
  };

  const perkCategories = {
    wellness: {
      icon: Heart,
      color: "text-green-400",
      bg: "bg-green-500/10",
    },
    growth: {
      icon: TrendingUp,
      color: "text-blue-400",
      bg: "bg-blue-500/10",
    },
    "work-life": {
      icon: Coffee,
      color: "text-purple-400",
      bg: "bg-purple-500/10",
    },
    financial: {
      icon: Award,
      color: "text-yellow-400",
      bg: "bg-yellow-500/10",
    },
  };

  // Animation variants
  const letterVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5 },
    },
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5 },
    },
  };

  // Split text into words for animation
  const animateText = (text: string, delay = 0) => {
    return text.split(" ").map((word, index) => (
      <motion.span
        key={index}
        variants={letterVariants}
        initial="hidden"
        animate={isHeroInView ? "visible" : "hidden"}
        transition={{ delay: delay + index * 0.1 }}
        className="inline-block mr-3"
      >
        {word}
      </motion.span>
    ));
  };

  return (
    <div className="min-h-screen bg-slate-950 text-white pt-20">
      {/* Animated Hero Section */}
      <section
        ref={heroRef}
        className="relative py-20 overflow-hidden"
      >
        <div className="absolute inset-0 bg-gradient-to-br from-purple-900/30 via-slate-900 to-cyan-900/30"></div>
        <div className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.02)_1px,transparent_1px)] bg-[size:50px_50px]"></div>

        {/* Animated background elements */}
        <motion.div
          className="absolute top-1/4 left-1/4 w-64 h-64 bg-purple-500/20 rounded-full blur-3xl"
          animate={{
            x: [0, 30, -30, 0],
            y: [0, -20, 20, 0],
            scale: [1, 1.1, 0.9, 1],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
        <motion.div
          className="absolute bottom-1/4 right-1/4 w-80 h-80 bg-cyan-500/20 rounded-full blur-3xl"
          animate={{
            x: [0, -25, 25, 0],
            y: [0, 25, -25, 0],
            scale: [1, 0.8, 1.2, 1],
          }}
          transition={{
            duration: 10,
            repeat: Infinity,
            ease: "easeInOut",
            delay: 2,
          }}
        />

        <div className="relative z-10 max-w-6xl mx-auto px-6 lg:px-8 text-center">
          {/* Mission badge */}
          <motion.div
            initial={{ opacity: 0, scale: 0.8, y: 30 }}
            animate={
              isHeroInView ? { opacity: 1, scale: 1, y: 0 } : {}
            }
            transition={{ duration: 0.6, delay: 0.2 }}
            className="inline-flex items-center space-x-3 glass rounded-full px-8 py-4 mb-12 border border-white/20 hover:border-purple-400/50 transition-all duration-500"
          >
            <motion.div
              animate={{ rotate: 360 }}
              transition={{
                duration: 8,
                repeat: Infinity,
                ease: "linear",
              }}
            >
              <Briefcase className="w-5 h-5 text-purple-400" />
            </motion.div>
            <span className="font-medium text-gray-200">
              {missionStatement.subtitle}
            </span>
          </motion.div>

          {/* Animated Mission Statement */}
          <div className="font-outfit text-4xl lg:text-6xl font-bold mb-8 leading-tight">
            <motion.h1
              className="mb-4"
              initial="hidden"
              animate={isHeroInView ? "visible" : "hidden"}
            >
              <span className="bg-gradient-to-r from-white via-purple-200 to-cyan-200 bg-clip-text text-transparent">
                {animateText("Join Our Mission:", 0.3)}
              </span>
            </motion.h1>
            <motion.h1
              initial="hidden"
              animate={isHeroInView ? "visible" : "hidden"}
              transition={{ delay: 0.8 }}
            >
              <span className="bg-gradient-to-r from-purple-400 via-pink-400 to-cyan-400 bg-clip-text text-transparent">
                {animateText("Crafting Digital", 0.6)}
              </span>
            </motion.h1>
            <motion.h1
              initial="hidden"
              animate={isHeroInView ? "visible" : "hidden"}
              transition={{ delay: 1.2 }}
            >
              <span className="bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent">
                {animateText("Experiences That Mesmerize", 0.9)}
              </span>
            </motion.h1>
          </div>

          <motion.p
            initial={{ opacity: 0, y: 30 }}
            animate={isHeroInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 1.5 }}
            className="text-xl text-gray-300 max-w-4xl mx-auto leading-relaxed mb-12"
          >
            {missionStatement.description}
          </motion.p>

          {/* Quick stats */}
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={isHeroInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 1, delay: 1.8 }}
            className="grid grid-cols-2 md:grid-cols-4 gap-6 max-w-4xl mx-auto"
          >
            {[
              {
                icon: Users,
                label: "Team Members",
                value: careerStats.employeeCount,
              },
              {
                icon: Globe,
                label: "Remote Workers",
                value: `${careerStats.remoteEmployees}%`,
              },
              {
                icon: Star,
                label: "Diversity Rate",
                value: `${careerStats.diversityPercentage}%`,
              },
              {
                icon: TrendingUp,
                label: "Avg. Tenure",
                value: careerStats.averageTenure,
              },
            ].map((stat, index) => (
              <motion.div
                key={stat.label}
                initial={{ opacity: 0, y: 30 }}
                animate={
                  isHeroInView ? { opacity: 1, y: 0 } : {}
                }
                transition={{
                  duration: 0.6,
                  delay: 2 + index * 0.1,
                }}
                className="glass rounded-2xl p-6 border border-white/10 hover:border-purple-400/30 transition-all duration-500 text-center"
              >
                <div className="w-12 h-12 mx-auto mb-4 gradient-electric rounded-xl flex items-center justify-center">
                  <stat.icon className="w-6 h-6 text-white" />
                </div>
                <div className="font-outfit text-2xl font-bold text-white mb-1">
                  {stat.value}
                </div>
                <div className="text-gray-400 text-sm">
                  {stat.label}
                </div>
              </motion.div>
            ))}
          </motion.div>

          {/* Values */}
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={isHeroInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 1, delay: 2.5 }}
            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mt-16 max-w-6xl mx-auto"
          >
            {missionStatement.values.map((value, index) => (
              <motion.div
                key={value.title}
                initial={{ opacity: 0, y: 30 }}
                animate={
                  isHeroInView ? { opacity: 1, y: 0 } : {}
                }
                transition={{
                  duration: 0.6,
                  delay: 2.7 + index * 0.1,
                }}
                className="text-center"
              >
                <h3 className="font-outfit text-lg font-bold text-white mb-3">
                  {value.title}
                </h3>
                <p className="text-gray-300 text-sm leading-relaxed">
                  {value.description}
                </p>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </section>

      {/* Job Listings with Accordion Animation */}
      <section ref={jobsRef} className="relative py-20">
        <div className="max-w-7xl mx-auto px-6 lg:px-8">
          {/* Section Header */}
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={isJobsInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <h2 className="font-outfit text-4xl lg:text-5xl font-bold mb-6">
              <span className="bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
