  content: '';
  position: absolute;
  inset: 0;
  background: linear-gradient(135deg, #8b5cf6, #06b6d4);
  animation: badgeGlow 2s ease-in-out infinite;
}
/* testimonial added */
.perspective {
  perspective: 1000px;
}
.transform-style-preserve-3d {
  transform-style: preserve-3d;
}
.backface-hidden {
  backface-visibility: hidden;
  -webkit-backface-visibility: hidden;
}

.rotate-y-180 {
  transform: rotateY(180deg);
}

.group:hover .hover\:rotate-y-180 {
  transform: rotateY(180deg);
}
.card-flip-container {
  perspective: 1000px;
}

.card-flip-inner {
  transition: transform 0.6s;
  transform-style: preserve-3d;
  position: relative;
}

.card-flip-container:hover .card-flip-inner {
  transform: rotateY(180deg);
}

.card-front,
.card-back {
  backface-visibility: hidden;
  -webkit-backface-visibility: hidden;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}

.card-back {
  transform: rotateY(180deg);
}

.glow-on-hover:hover {
  box-shadow:
    0 0 10px rgba(168, 85, 247, 0.8),
    0 0 20px rgba(168, 85, 247, 0.6),
    0 0 30px rgba(168, 85, 247, 0.4);
  transition: box-shadow 0.3s ease-in-out;
}
.glow-on-hover {
  position: relative; /* Needed for shadows */
  z-index: 1;         /* Raise above siblings */
  transform-style: flat !important; /* Optional: try to disable 3d for glow layer */
}


/* Responsive adjustments for careers page */
@media (max-width: 768px) {
  .culture-grid:hover {
    transform: translateY(-4px) scale(1.01);
  }

  .perk-card:hover {
    transform: translateY(-4px);
  }

  .job-listing:hover {
    transform: translateY(-2px);
  }
}

/* Responsive Card System Utilities */
.responsive-card {
  display: flex;
  flex-direction: column;
  width: 100%;
  max-width: 100%;
  min-width: 0; /* Prevents flex items from overflowing */
  word-wrap: break-word;
  overflow-wrap: break-word;
}

/* Responsive container queries support */
@container (max-width: 320px) {
  .responsive-card {
    padding: 0.75rem;
    font-size: 0.875rem;
  }

  .responsive-card h3 {
    font-size: 1rem;
    line-height: 1.25;
  }
}

@container (min-width: 480px) {
  .responsive-card {
    padding: 1.5rem;
  }
}

/* Fluid typography for responsive cards */
.fluid-text-sm {
  font-size: clamp(0.75rem, 2.5vw, 0.875rem);
}

.fluid-text-base {
  font-size: clamp(0.875rem, 3vw, 1rem);
}

.fluid-text-lg {
  font-size: clamp(1rem, 3.5vw, 1.125rem);
}

.fluid-text-xl {
  font-size: clamp(1.125rem, 4vw, 1.25rem);
}

.fluid-text-2xl {
  font-size: clamp(1.25rem, 4.5vw, 1.5rem);
}

.fluid-text-3xl {
  font-size: clamp(1.5rem, 5vw, 1.875rem);
}

/* Responsive spacing utilities */
.responsive-padding {
  padding: clamp(0.5rem, 2.5vw, 1.5rem);
}

.responsive-margin {
  margin: clamp(0.5rem, 2.5vw, 1.5rem);
}

.responsive-gap {
  gap: clamp(0.5rem, 2.5vw, 1.5rem);
}

/* Enhanced responsive grid utilities */
.grid-responsive-sm {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1rem;
}

.grid-responsive-md {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1.5rem;
}

.grid-responsive-lg {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
}

/* Masonry responsive utilities */
.masonry-responsive {
  column-count: 1;
  column-gap: 1rem;
}

@media (min-width: 640px) {
  .masonry-responsive {
    column-count: 2;
    column-gap: 1.5rem;
  }
}

@media (min-width: 1024px) {
  .masonry-responsive {
    column-count: 3;
    column-gap: 2rem;
  }
}

@media (min-width: 1280px) {
  .masonry-responsive {
    column-count: 4;
    column-gap: 2rem;
  }
}

.masonry-item {
  break-inside: avoid;
  margin-bottom: 1rem;
}

@media (min-width: 640px) {
  .masonry-item {
    margin-bottom: 1.5rem;
  }
}

@media (min-width: 1024px) {
  .masonry-item {
    margin-bottom: 2rem;
  }
}

/* Responsive image containers */
.responsive-image-container {
  position: relative;
  width: 100%;
  height: 0;
  overflow: hidden;
}

.responsive-image-container.aspect-square {
  padding-bottom: 100%;
}

.responsive-image-container.aspect-video {
  padding-bottom: 56.25%;
}

.responsive-image-container.aspect-4-3 {
  padding-bottom: 75%;
}

.responsive-image-container.aspect-3-2 {
  padding-bottom: 66.67%;
}

.responsive-image-container img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* Enhanced card hover effects for touch devices */
@media (hover: none) and (pointer: coarse) {
  .responsive-card:hover {
    transform: none;
  }

  .responsive-card:active {
    transform: scale(0.98);
    transition: transform 0.1s ease;
  }
}

/* Improved focus states for accessibility */
.responsive-card:focus-within {
  outline: 2px solid rgba(139, 92, 246, 0.5);
  outline-offset: 2px;
}

/* Print styles for responsive cards */
@media print {
  .responsive-card {
    break-inside: avoid;
    page-break-inside: avoid;
    box-shadow: none;
    border: 1px solid #ddd;
    background: white !important;
    color: black !important;
    padding: 1rem;
    margin-bottom: 1rem;
  }

  .responsive-card * {
    color: black !important;
    background: transparent !important;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .responsive-card {
    border: 2px solid;
    background: var(--background);
    color: var(--foreground);
  }

  .responsive-card:hover {
    border-color: var(--primary);
  }
}

/* Reduced motion support for responsive cards */
@media (prefers-reduced-motion: reduce) {
  .responsive-card,
  .responsive-card * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }

  .responsive-card:hover {
    transform: none !important;
  }
}

/* Dark mode specific responsive card styles */
@media (prefers-color-scheme: dark) {
  .responsive-card {
    border-color: rgba(255, 255, 255, 0.1);
  }

  .responsive-card:hover {
    border-color: rgba(255, 255, 255, 0.3);
    background-color: rgba(255, 255, 255, 0.05);
  }
}

/* Universal Floating Nav Enhancements */
@keyframes fabGlow {
  0%, 100% {
    box-shadow: 0 0 20px rgba(139, 92, 246, 0.3);
  }
  50% {
    box-shadow: 0 0 30px rgba(139, 92, 246, 0.6), 0 0 50px rgba(6, 182, 212, 0.3);
  }
}

@keyframes floatUpDown {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-2px);
  }
}

@keyframes quickActionSlide {
  from {
    transform: translateX(50px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.fab-main {
  animation: floatUpDown 3s ease-in-out infinite;
}

.fab-main:hover {
  animation: fabGlow 2s ease-in-out infinite;
}

.quick-action-enter {
  animation: quickActionSlide 0.3s cubic-bezier(0.4, 0, 0.2, 1) forwards;
}

/* Calculator Modal Styles */
.calculator-feature-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 1rem;
}

.calculator-price-display {
  background: linear-gradient(135deg, 
    rgba(139, 92, 246, 0.1) 0%, 
    rgba(6, 182, 212, 0.1) 100%
  );
  border: 1px solid rgba(139, 92, 246, 0.3);
}

.calculator-feature-card {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
}

.calculator-feature-card:hover {
  transform: translateY(-2px);
}

.calculator-feature-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 2px;
  background: linear-gradient(90deg, transparent, rgba(139, 92, 246, 0.8), transparent);
  transition: left 0.5s;
}

.calculator-feature-card:hover::before {
  left: 100%;
}

/* Floating nav pulse for notifications */
.notification-pulse {
  animation: fabPulse 2s infinite;
}

/* Support button special animation */
@keyframes supportBounce {
  0%, 100% {
    transform: translateY(0) scale(1);
  }
  25% {
    transform: translateY(-3px) scale(1.05);
  }
  50% {
    transform: translateY(-1px) scale(1.02);
  }
  75% {
    transform: translateY(-2px) scale(1.03);
  }
}

.support-active {
  animation: supportBounce 1s ease-in-out infinite;
}

/* Responsive adjustments for floating nav */
@media (max-width: 768px) {
  .fab-main {
    width: 56px;
    height: 56px;
  }

  .quick-action-button {
    width: 44px;
    height: 44px;
  }

  .calculator-feature-grid {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 640px) {
  .calculator-modal {
    margin: 1rem;
    max-height: calc(100vh - 2rem);
  }
}

/* Accessibility improvements */
@media (prefers-reduced-motion: reduce) {
  .animate-marquee,
  .animate-float,
  .animate-pulse-glow,
  .animate-draw-line,
  .animate-scale-in,
  .animate-count-up,
  .ripple-effect,
  .map-zoom,
  .floating-pin,
  .form-input-focused,
  .accordion-slide,
  .culture-image-zoom,
  .overlay-slide,
  .perk-float,
  .icon-bounce,
  .job-card-hover,
  .department-filter,
  .badge-glow {
    animation: none;
  }

  * {
    transition-duration: 0.01ms !important;
  }

  .team-member img {
    filter: grayscale(0%);
  }

  .magnetic-strong:hover {
    transform: none;
  }

  .culture-grid:hover,
  .perk-card:hover,
  .job-listing:hover {
    transform: none;
  }
}

