'use client'

import { motion, AnimatePresence } from 'motion/react'
import { X } from 'lucide-react'
import { useEffect } from 'react'

interface PopupModalProps {
  isOpen: boolean
  onClose: () => void
  children: React.ReactNode
  title?: string
  maxWidth?: string
}

export function PopupModal({ 
  isOpen, 
  onClose, 
  children, 
  title,
  maxWidth = "max-w-4xl" 
}: PopupModalProps) {
  // Close on escape key
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose()
    }

    if (isOpen) {
      document.addEventListener('keydown', handleEscape)
      document.body.style.overflow = 'hidden'
    }

    return () => {
      document.removeEventListener('keydown', handleEscape)
      document.body.style.overflow = 'unset'
    }
  }, [isOpen, onClose])

  return (
    <AnimatePresence>
      {isOpen && (
        <>
          {/* Backdrop */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={onClose}
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50"
          />

          {/* Modal */}
          <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
            <motion.div
              initial={{ opacity: 0, scale: 0.9, y: 20 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.9, y: 20 }}
              transition={{ 
                type: "spring", 
                stiffness: 300, 
                damping: 25 
              }}
              className={`relative w-full ${maxWidth} max-h-[90vh] overflow-hidden`}
            >
              {/* Modal Content */}
              <div className="glass border border-white/20 rounded-2xl shadow-2xl relative">
                {/* Header */}
                <div className="flex items-center justify-between p-6 border-b border-white/10">
                  {title && (
                    <h2 className="font-outfit text-2xl font-bold bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent">
                      {title}
                    </h2>
                  )}
                  <motion.button
                    onClick={onClose}
                    className="ml-auto p-2 hover:bg-white/10 rounded-xl transition-colors duration-300"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <X className="w-6 h-6 text-gray-400 hover:text-white" />
                  </motion.button>
                </div>

                {/* Content */}
                <div className="max-h-[70vh] overflow-y-auto">
                  {children}
                </div>
              </div>
            </motion.div>
          </div>
        </>
      )}
    </AnimatePresence>
  )
}

client/src/marketing/hooks/usePopupManag.content:
'use client'

import { useState, useCallback } from 'react'

export type PopupType = 
  | 'web-development'
  | 'mobile-app' 
  | 'ecommerce'
  | 'backend-systems'
  | 'ui-ux-design'
  | 'security-devops'
  | null

interface PopupState {
  isOpen: boolean
  type: PopupType
}

export function usePopupManager() {
  const [popup, setPopup] = useState<PopupState>({
    isOpen: false,
    type: null
  })

  const openPopup = useCallback((type: PopupType) => {
    setPopup({
      isOpen: true,
      type
    })

    // Prevent body scroll when popup is open
    document.body.style.overflow = 'hidden'
  }, [])

  const closePopup = useCallback(() => {
    setPopup({
      isOpen: false,
      type: null
    })

    // Restore body scroll
    document.body.style.overflow = 'unset'
  }, [])

  const isPopupOpen = (type: PopupType) => {
    return popup.isOpen && popup.type === type
  }

  return {
    popup,
    openPopup,
    closePopup,
    isPopupOpen,
    isAnyPopupOpen: popup.isOpen
  }
}

